<template>
    <div class="log">
        <h1>{{msg}}</h1>
        <b-form @submit="onSubmit">
            <div class="from-group">
                <label for='username'>Usernmae</label>
                <input type="text" v-model="username" name="username" class="form-control" :class="{ 'is-invalid': submitted && !username }" />
                <div v-show="submitted && !username" class="invalid-feedback">Username is required</div>
            </div><br>
            <div class="form-group">
                <label htmlFor="password">Password</label>
                <input type="password" v-model="password" name="password" class="form-control" :class="{ 'is-invalid': submitted && !password }" />
                <div v-show="submitted && !password" class="invalid-feedback">Password is required</div>
            </div><br>
            <div class="form-group">
                <a href="" v-on:click="regis" >Regisrter</a>
                <button class="btn btn-primary" v-on:click="submit">Login</button>
                <!-- <img v-show="status.loggingIn" src="data:image/gif;base64," />
                <router-link to="/register" class="btn btn-link">Register</router-link> -->
            </div><br>
        </b-form>
    </div>
</template>

<script>
import axios from 'axios'
export default {
  name: 'login',
  data() {
    return {
      msg: 'Hi! IM ENBIP',
      login:{},
    };
  },
  methods: {
    regis(){
        this.$router.push({name:"register"});
        this.$refs.form.reset();
         },
    onSubmit (evt) {
      evt.preventDefault()
      axios.post(`http://localhost:3000/api/auth/login/`, this.login)
      .then(response => {
        localStorage.setItem('jwtToken', response.data.token)
        this.$router.push({
          name: 'BookList'
        })
      })
      .catch(e => {
        console.log(e)
        this.errors.push(e)
      })
    },
    }
};
</script>